import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { LogOut } from 'lucide-react';
import MindMapDashboard from '../pages/mindmapdashboard.jsx';
import MindMap from '../pages/mindmap.jsx';
import './mindmapdashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [session, setSession] = useState(null);
  const [loadingSession, setLoadingSession] = useState(true);
  const [currentDisplayedTitle, setCurrentDisplayedTitle] = useState('Loading...'); // Initial title displayed in header

  // --- Session Loading ---
  useEffect(() => {
    const storedSession = localStorage.getItem('mindmapSession');
    if (storedSession) {
      try {
        const parsedSession = JSON.parse(storedSession);
        if (parsedSession && parsedSession.token && parsedSession.user) {
          setSession(parsedSession);
        } else {
          localStorage.removeItem('mindmapSession'); // Clear invalid session
          navigate('/'); // Redirect to login if session is malformed
        }
      } catch (e) {
        console.error("Failed to parse session from localStorage in Dashboard", e);
        localStorage.removeItem('mindmapSession');
        navigate('/'); // Redirect to login if parsing fails
      }
    } else {
      navigate('/'); // No session found, redirect to login
    }
    setLoadingSession(false); // Done attempting to load session
  }, [navigate]);

  // --- Logout Handler ---
  const handleLogout = useCallback(() => {
    setSession(null);
    localStorage.removeItem('mindmapSession');
    navigate('/'); // Redirect to login page after logout
  }, [navigate]);

  // --- Callback for Child Components to Set Header Title ---
  // This function is passed down to MindMap and MindMapDashboard
  const handleSetDashboardTitle = useCallback((title) => {
    setCurrentDisplayedTitle(title);
  }, []); // No dependencies, as it only sets state

  // --- Loading State Display ---
  if (loadingSession) {
    return <div className="dashboard-loading">Loading dashboard...</div>;
  }

  // --- Fallback if Session is Missing (should be caught by ProtectedRoute first) ---
  if (!session) {
    return null; // Or a simple spinner/message if you want
  }

  // --- Conditional Rendering of Child Components ---
  const renderChildComponent = () => {
    if (location.pathname.startsWith('/mindmap/')) {
      return (
        <MindMap
          session={session}
          onLogout={handleLogout}
          onSetDashboardTitle={handleSetDashboardTitle} // Pass the title setter to MindMap
        />
      );
    } else if (location.pathname === '/dashboard') {
      return (
        <MindMapDashboard
          session={session}
          onLogout={handleLogout}
          onSetDashboardTitle={handleSetDashboardTitle} // Pass the title setter to MindMapDashboard
        />
      );
    }
    // Default content if no specific route matches, or initial display
    return <div className="dashboard-default-content">Please select a section from the navigation.</div>;
  };

  return (
    <div className="dashboard-layout">
      <header className="dashboard-header">
        {/* The dynamic title is displayed here */}
        <h1 className="dashboard-title">{currentDisplayedTitle}</h1>
        <div className="user-controls">
          {session.user && <span className="user-email">{session.user.email}</span>}
          <button onClick={handleLogout} className="logout-btn" title="Logout">
            <LogOut className="logout-icon" /> Logout
          </button>
        </div>
      </header>
      <main className="dashboard-content">
        {renderChildComponent()}
      </main>
    </div>
  );
};

export default Dashboard;
