import React, { useState, useEffect, useRef } from 'react';
import { Plus, Edit, Trash2, Save, UploadCloud, FilePlus, LogOut, ChevronDown, ChevronUp, RefreshCw, XCircle } from 'lucide-react';
import './mindmaptoolbar.css';

const MindMapToolbar = ({
  onAddNode,
  onEditNode,
  onDeleteNode,
  selectedNode,
  isRootSelected,
  onSave,
  onLoadMap, // Re-added
  onNewMap, // Re-added
  userMindMaps, // Re-added
  currentMindMapId, // Re-added
  loading,
  message,
  onLogout, // Re-added
  userName, // Re-added
  currentMapTitle, // Re-added
  onSetMapTitle // Re-added
}) => {
  const isNodeSelected = selectedNode !== null;
  const [showLoadDropdown, setShowLoadDropdown] = useState(false); // Re-added
  const [isEditingTitle, setIsEditingTitle] = useState(false); // Re-added
  const [editableTitle, setEditableTitle] = useState(currentMapTitle); // Re-added

  const loadDropdownRef = useRef(null); // Re-added
  const titleInputRef = useRef(null); // Re-added

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loadDropdownRef.current && !loadDropdownRef.current.contains(event.target)) {
        setShowLoadDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Focus the input when editing starts
  useEffect(() => {
    if (isEditingTitle && titleInputRef.current) {
      titleInputRef.current.focus();
    }
  }, [isEditingTitle]);

  // Update editable title when currentMapTitle prop changes from parent
  useEffect(() => {
    setEditableTitle(currentMapTitle);
  }, [currentMapTitle]);

  const handleLoadMapClick = (mapId) => { // Re-added
    onLoadMap(mapId);
    setShowLoadDropdown(false);
  };

  const handleTitleChange = (e) => { // Re-added
    setEditableTitle(e.target.value);
  };

  const handleTitleBlur = () => { // Re-added
    const trimmedTitle = editableTitle.trim();
    if (trimmedTitle === '' || trimmedTitle === 'Untitled Map') {
      setEditableTitle(currentMapTitle || 'Untitled Map');
    } else {
      onSetMapTitle(trimmedTitle);
    }
    setIsEditingTitle(false);
  };

  const handleTitleKeyDown = (e) => { // Re-added
    if (e.key === 'Enter') {
      e.preventDefault();
      handleTitleBlur();
    } else if (e.key === 'Escape') {
      setEditableTitle(currentMapTitle);
      setIsEditingTitle(false);
      e.preventDefault();
    }
  };

  return (
    <div className="mindmap-toolbar-wrapper">
      <div className="mindmap-toolbar">
        {/* Map Actions Section (with New/Load/Save) */}
        <div className="toolbar-section map-actions"> {/* Re-added map-actions */}
          <button onClick={onNewMap} className="btn btn-success" title="New Mind Map">
            <FilePlus className="btn-icon" /> New
          </button>
          <button onClick={onSave} className="btn btn-primary" disabled={loading} title="Save Current Mind Map">
            {loading ? <RefreshCw className="btn-icon spin" /> : <Save className="btn-icon" />} Save
          </button>
          <div className="dropdown-container" ref={loadDropdownRef}>
            <button onClick={() => setShowLoadDropdown(!showLoadDropdown)} className="btn btn-info" title="Load Mind Map">
              <UploadCloud className="btn-icon" /> Load
              {showLoadDropdown ? <ChevronUp className="dropdown-arrow" /> : <ChevronDown className="dropdown-arrow" />}
            </button>
            {showLoadDropdown && (
              <div className="dropdown-menu">
                {userMindMaps && userMindMaps.length > 0 ? (
                  userMindMaps.sort((a,b) => new Date(b.updatedAt) - new Date(a.updatedAt)).map((map) => (
                    <button
                      key={map._id}
                      onClick={() => handleLoadMapClick(map._id)}
                      className={`dropdown-item ${map._id === currentMindMapId ? 'active' : ''}`}
                    >
                      {map.title || 'Untitled'}
                      <small>{new Date(map.updatedAt).toLocaleDateString()}</small>
                    </button>
                  ))
                ) : (
                  <div className="dropdown-item no-maps">No saved maps</div>
                )}
                <div className="dropdown-footer">
                   <button onClick={() => setShowLoadDropdown(false)} className="btn-close-dropdown">
                     <XCircle size={16} /> Close
                   </button>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Node Actions Section */}
        <div className="toolbar-section node-actions">
          <button
            onClick={onAddNode}
            className="btn btn-add"
            disabled={!isNodeSelected && !isRootSelected}
            title="Add Child Node to Selected Idea"
          >
            <Plus className="btn-icon" /> Add Idea
          </button>
          <button
            onClick={onEditNode}
            className="btn btn-edit"
            disabled={!isNodeSelected}
            title="Edit Selected Idea's Content"
          >
            <Edit className="btn-icon" /> Edit Idea
          </button>
          <button
            onClick={onDeleteNode}
            className="btn btn-delete"
            disabled={!isNodeSelected || isRootSelected}
            title="Delete Selected Idea (Root cannot be deleted)"
          >
            <Trash2 className="btn-icon" /> Delete Idea
          </button>
        </div>

        {/* Current Map Info section (title editing) */}
        <div className="toolbar-section current-map-info"> {/* Re-added current-map-info */}
          {isEditingTitle ? (
            <input
              ref={titleInputRef}
              type="text"
              value={editableTitle}
              onChange={handleTitleChange}
              onBlur={handleTitleBlur}
              onKeyDown={handleTitleKeyDown}
              className="map-title-input"
              aria-label="Edit map title"
            />
          ) : (
            <span className="map-title-display" onClick={() => setIsEditingTitle(true)} title="Click to edit map title">
              {currentMapTitle || "Untitled Map"}
            </span>
          )}
        </div>

        {/* User Info & Logout */}
        <div className="toolbar-section user-info"> {/* Re-added user-info */}
          {userName && <span className="user-name" title={`Logged in as ${userName}`}>{userName}</span>}
          <button onClick={onLogout} className="btn btn-logout" title="Log out of your account">
            <LogOut className="btn-icon" /> Logout
          </button>
        </div>
      </div>
      {/* Message Area */}
      {message && <div className={`toolbar-message ${loading ? 'loading' : ''}`}>{message}</div>}
    </div>
  );
};

export default MindMapToolbar;
