import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import MindMap from './pages/mindmap.jsx';
import MindMapDashboard from './pages/mindmapdashboard.jsx';
import Auth from './pages/auth.jsx';
import HomePage from './pages/HomePage.jsx'; // Keep HomePage if you want it
import ProtectedRoute from './components/ProtectedRoute.jsx'; // Keep ProtectedRoute

function App() {
  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Auth />} /> {/* This remains your login/register page */}
        <Route path="/home" element={<HomePage />} /> {/* Keep HomePage if you want it */}

        {/* Protected Routes - Directly render MindMapDashboard and MindMap */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <MindMapDashboard /> {/* Directly render MindMapDashboard */}
            </ProtectedRoute>
          }
        />
        <Route
          path="/mindmap/:id"
          element={
            <ProtectedRoute>
              <MindMap /> {/* Directly render MindMap */}
            </ProtectedRoute>
          }
        />

        {/* Fallback for any unmatched routes - redirects to the login/auth page */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
