import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Auth from './pages/auth.jsx';
import HomePage from './pages/homepage.jsx'; // <--- NEW IMPORT
import ProtectedRoute from './components/ProtectedRoute.jsx';
import Dashboard from './components/mindmapdashboard.jsx';

function App() {
  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Auth />} /> {/* This remains your login/register page */}
        <Route path="/home" element={<HomePage />} /> {/* <--- NEW HOME PAGE ROUTE */}

        {/* Protected Routes - All now go through the Dashboard wrapper */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/mindmap/:id"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />

        {/* Fallback for any unmatched routes - redirects to the login/auth page */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
