import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { LogOut } from 'lucide-react';
import MindMapDashboard from '../pages/mindmapdashboard.jsx';
import MindMap from '../pages/mindmap.jsx';
import './mindmapdashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [session, setSession] = useState(null);
  const [loadingSession, setLoadingSession] = useState(true);
  const [currentDisplayedTitle, setCurrentDisplayedTitle] = useState('Loading...');

  // --- Session Loading ---
  useEffect(() => {
    const storedSession = localStorage.getItem('mindmapSession');
    if (storedSession) {
      try {
        const parsedSession = JSON.parse(storedSession);
        if (parsedSession && parsedSession.token && parsedSession.user) {
          setSession(parsedSession);
        } else {
          localStorage.removeItem('mindmapSession');
          navigate('/');
        }
      } catch (e) {
        console.error("Failed to parse session from localStorage in Dashboard", e);
        localStorage.removeItem('mindmapSession');
        navigate('/');
      }
    } else {
      navigate('/');
    }
    setLoadingSession(false);
  }, [navigate]);

  // --- Logout Handler ---
  const handleLogout = useCallback(() => {
    setSession(null);
    localStorage.removeItem('mindmapSession');
    navigate('/');
  }, [navigate]);

  // --- Callback for Child Components to Set Header Title ---
  const handleSetDashboardTitle = useCallback((title) => {
    setCurrentDisplayedTitle(title);
  }, []);

  // --- Navigation Handlers for Header Links ---
  const goToMindMapDashboard = useCallback(() => {
    navigate('/dashboard');
  }, [navigate]);

  const goToHomePage = useCallback(() => {
    navigate('/home');
  }, [navigate]);

  // --- Loading State Display ---
  if (loadingSession) {
    return <div className="dashboard-loading">Loading dashboard...</div>;
  }

  // --- Fallback if Session is Missing ---
  if (!session) {
    return null;
  }

  // --- Conditional Rendering of Child Components ---
  const renderChildComponent = () => {
    if (location.pathname.startsWith('/mindmap/')) {
      return (
        <MindMap
          session={session}
          onLogout={handleLogout}
          onSetDashboardTitle={handleSetDashboardTitle}
        />
      );
    } else if (location.pathname === '/dashboard') {
      return (
        <MindMapDashboard
          session={session}
          onLogout={handleLogout}
          onSetDashboardTitle={handleSetDashboardTitle}
        />
      );
    }
    return <div className="dashboard-default-content">Select a section from the navigation.</div>;
  };

  return (
    <div className="dashboard-layout">
      <header className="dashboard-header">
        {/* Left-aligned navigation links */}
        <div className="dashboard-nav-links">
          <span className="nav-link" onClick={goToHomePage} title="Go to Home Page">Home</span>
          <span className="nav-link" onClick={goToMindMapDashboard} title="Go to Your Mind Maps">My Maps</span>
        </div>

        {/* Center-aligned dynamic title */}
        <h1 className="dashboard-title">{currentDisplayedTitle}</h1>

        {/* Right-aligned user controls */}
        <div className="user-controls">
          {session.user && <span className="user-email">{session.user.email}</span>}
          <button onClick={handleLogout} className="logout-btn" title="Logout">
            <LogOut className="logout-icon" /> Logout
          </button>
        </div>
      </header>
      <main className="dashboard-content">
        {renderChildComponent()}
      </main>
    </div>
  );
};

export default Dashboard;
